var testController = angular.module("testController", []);
testController.controller("homeCtrl", ["$scope", function (e) {
    e.pageClass = "page-home", e.counts = [10, 20, 30, 40, 50], e.selectFun = function (t) {
        e.selectVal = t.target.innerHTML
    }
}]);
testController.controller("navCtrl", ["$scope", function (e) {
    e.pageClass = "page-nav"
}]);
testController.controller("navigationCtrl", ["$scope", function (a) {
    a.pageClass = "page-navigation", a.id = 0, a.href = location.hash.replace(/\//g, ",").split(","), a.navs = [
        {id: 1, path: "today", text: "今日活动",className: !1},
        {id: 2, path: "site", text: "场地管理", className: !1},
        {id: 3, path: "session", text: "场次管理", className: !1},
        {id: 4, path: "personnel", text: "人员管理", className: !1},
        {id: 5, path: "preset", text: "预置弹幕管理", className: !1},
        {id: 6, path: "danmu", text: "弹幕管理", className: !1}
    ];

    for (var s = 0; s < a.navs.length; s++)a.navs[s].path == a.href[1] && (a.navs[s].className = !0);
    a.click = function (s) {
        a.navs[a.id].className = !1, a.navs[s - 1].className = !0, a.id = s - 1
    }
}]);
testController.controller("personnelCtrl", ["$scope", function (e) {
    e.pageClass = "page-personnel"
}]);
testController.controller("presetCtrl", ["$scope", function (e) {
    e.pageClass = "page-preset"
}]);
testController.controller("sessionCtrl", ["$scope", function (s) {
    s.pageClass = "page-session"
}]);
testController.controller("siteCtrl", ["$scope", function (a) {
    a.pageClass = "page-site", a.citys = $city, a.citysName = [], a.cityName = function (e) {
        a.province = a.citys[e].name, a.area(e)
    }, a.area = function (e) {
        if (a.citysName && (a.citysName.length = 0), a.citys[e].name == a.citys[e].city[0].name)a.areas = a.citys[e].city[0].area, a.citysName = a.areas; else {
            for (var t = 0; t < a.citys[e].city.length; t++)a.citysName.push(a.citys[e].city[t].name);
            a.areas = a.citysName
        }
    }, a.cityName(0), a.areasName = function (e) {
        a.areaName = a.citysName[e]
    }, a.areasName(0)
}]);
testController.controller("todayCtrl", ["$scope", function (o) {
    o.pageClass = "page-today"
}]);

